import React from 'react';
import { Link } from 'react-router-dom';
import Sidebar from './sidebar';
import '../css/home.css'; // Thêm CSS cho trang chủ nếu cần

const Home = ({ products }) => {
  return (
    <div className="home-container">
      <h1>Trang Chủ</h1>
      
      {/* Thêm video tự động chạy */}
      <div className="video-container">
        <video autoPlay muted loop className="home-video">
        <source src="/video/Untitled video - Made with Clipchamp.mp4" type="video/mp4" />
          Trình duyệt của bạn không hỗ trợ video.
        </video>
      </div>

      <div className="home-sidebar">
        <Sidebar />
      </div>
      <div className="product-list">
        {products.map(product => (
          <div key={product.id} className="product-item">
            <Link to={`/products/${product.id}`} className="product-link">
              <img src={product.image} alt={product.name} className="product-image" />
            </Link>
            <div className="product-info">
              <h2 className="product-name">{product.name}</h2>
              <p className="product-price">{product.price.toLocaleString()} VND</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
