import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Đảm bảo rằng bạn đã cài đặt axios
import '../../css/login.css';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); // Thêm trạng thái lỗi
  const navigate = useNavigate(); // Sử dụng useNavigate

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const response = await axios.post('http://localhost:3000/api/auth/login', { username, password });
      alert(response.data.message); // Hiển thị thông báo từ server

      // Chuyển hướng về trang chủ
      navigate('/'); // Chuyển hướng sau khi đăng nhập thành công
    } catch (error) {
          console.log(error); // Log lỗi để kiểm tra
      if (error.response) {
        setError(error.response.data.message); // Hiển thị lỗi từ server
      } else {
        setError('Đăng nhập thất bại, vui lòng thử lại!'); // Lỗi khác
      }
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Classy Login Form</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-box">
            <span className="icon">&#128100;</span>
            <input
              type="text"
              placeholder="User name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="input-box">
            <span className="icon">&#128274;</span>
            <input
            type={showPassword ? 'text' : 'password'} // Thay đổi loại input
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
             <label>
              <input 
                type="checkbox" 
                checked={showPassword} 
                onChange={() => setShowPassword(!showPassword)} 
              />
              Hiển thị mật khẩu
            </label>
          </div>
          {error && <div className="error-message">{error}</div>} {/* Hiển thị thông báo lỗi */}
          <div className="options">
            <label>
              <input type="checkbox" /> Remember me
            </label>
            <a href="#">Forgot Password?</a>
          </div>
          <button type="submit" className="login-btn">Login</button>
          <div className="or">or</div>
          <div className="social-login">
            <button className="facebook" type="button">Facebook</button>
            <button className="twitter" type="button">Twitter</button>
          </div>
          <div className="register-link">
            <button type="button" onClick={() => navigate('/register')}>Register</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
