import React from 'react';
import { Link } from 'react-router-dom';
import Sidebar from './sidebar';
import '../css/home.css'; // Thêm CSS cho trang chủ nếu cần
import VideoEmbed from '../css/video.css';
import HomeVideo from './video'; // Đảm bảo đường dẫn chính xác
const Home = ({ products }) => {
  return (
    <div className="home-container">
      <h1>Trang Chủ</h1>
      
      <HomeVideo /> {/* Chèn video vào trang chính */}

      <div className="home-sidebar">
        <Sidebar />
      </div>
      <div className="product-list">
        {products.map(product => (
          <div key={product.id} className="product-item">
            <Link to={`/products/${product.id}`} className="product-link">
              <img src={product.image} alt={product.name} className="product-image" />
            </Link>
            <div className="product-info">
              <h2 className="product-name">{product.name}</h2>
              <p className="product-price">{product.price.toLocaleString()} VND</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
