import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../../css/productDetail.css';

const ProductDetail = ({ products, addToCart }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [selectedSize, setSelectedSize] = useState('M');
  const [quantity, setQuantity] = useState(1); // Thêm trạng thái cho số lượng
  const [notification, setNotification] = useState(null);

  const product = products.find(p => p.id === Number(id));

  if (!product) {
    return <div>Product not found</div>;
  }

  const handleAddToCart = () => {
    const cartProduct = { ...product, size: selectedSize, quantity }; // Thêm số lượng vào giỏ hàng
    addToCart(cartProduct);
    showNotification(cartProduct);
  };

  const showNotification = (product) => {
    setNotification(product);
    setTimeout(() => {
      setNotification(null);
    }, 3000);
  };

  const handleSizeChange = (e) => {
    setSelectedSize(e.target.value);
  };

  const handleQuantityChange = (e) => {
    setQuantity(Number(e.target.value)); // Cập nhật số lượng
  };

  return (
    <div className="product-detail">
      <button onClick={() => navigate('/')}>Trở về danh sách</button>
      <div className='product-content'>
        <img src={product.image} alt={product.name} />

        <div>
          <h2>{product.name}</h2>
          <p>Giá: {product.price.toLocaleString()} VND</p>
          <label htmlFor="size-select">Kích thước:</label>
          <select id="size-select" value={selectedSize} onChange={handleSizeChange}>
            <option value="S">S</option>
            <option value="M">M</option>
            <option value="L">L</option>
            <option value="XL">XL</option>
          </select>

          <label htmlFor="quantity-select">Số lượng:</label>
          <input 
            type="number" 
            id="quantity-select" 
            value={quantity} 
            onChange={handleQuantityChange} 
            min="1" // Đảm bảo số lượng tối thiểu là 1
          />

          <button onClick={handleAddToCart}>Thêm vào giỏ</button>

          {notification && (
            <div className="notification">
              <img src={notification.image} alt={notification.name} className="notification-img" />
              <div className="notification-info">
                <h3>{notification.name}</h3>
                <p>Kích thước: {notification.size}</p>
                <p>Số lượng: {notification.quantity}</p> {/* Hiển thị số lượng */}
                <p>Giá: {(notification.price * notification.quantity).toLocaleString()} VND</p> {/* Giá tổng */}
              </div>
              <button className="view-cart-button" onClick={() => navigate('/cart')}>Xem giỏ hàng</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
