import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../home.css'; // Thêm CSS cho trang chủ nếu cần

const Home = ({ products, onViewDetail }) => {
  const navigate = useNavigate();

  const handleViewDetail = (product) => {
    // Chuyển hướng đến trang chi tiết sản phẩm
    navigate(`/products/${product.id}`);
  };

  return (
    <div className="home-container">
      <h1>Trang Chủ</h1>
      <div className="product-list">
        {products.map(product => (
          <div key={product.id} className="product-item">
            <img src={product.image} alt={product.name} className="product-image" />
            <div className="product-info">
              <h2 className="product-name">{product.name}</h2>
              <p className="product-price">{product.price.toLocaleString()} VND</p>
              <button className="product-detail-button" onClick={() => handleViewDetail(product)}>
                Xem Chi Tiết
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
