import React from 'react';
import { useNavigate } from 'react-router-dom'; // Đảm bảo import useNavigate
import HomeVideo from './video'; // Import component video
import ProductCard from './productCard'; // Import ProductCard component
import '../css/home.css'; // CSS cho trang chủ
import '../css/video.css';

const Home = ({ products }) => {
  const navigate = useNavigate(); // Khai báo useNavigate

  const handleViewDetail = (product) => {
    navigate(`/products/${product.id}`);
  };

  return (
    <div className="home-container">
      <div className="navbar">
        {/* Navbar content here */}
      </div>
      
      <div className="video-container">
        <HomeVideo /> {/* Chèn video vào trang chính */}
      </div>
      
      <div className="home-content">
        <div className="product-list">
          {products.map((product) => (
            <ProductCard
              key={product.id}
              frontImage={product.image}
              backImage="https://example.com/back-image.jpg" // Thay thế bằng đường dẫn hình ảnh mặt sau nếu có
              description={`Tên sản phẩm: ${product.name}`} // Cung cấp mô tả sản phẩm
              onClick={() => handleViewDetail(product)} // Thêm onClick event
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
