import React from 'react';

const Cart = ({ cartItems }) => {
  return (
    <div>
      <h2>Giỏ hàng</h2>
      <ul>
        {cartItems.map((item, index) => (
          <li key={index}>
            <h3>{item.name}</h3>
            <p>Giá: {item.price} VNĐ</p>
          </li>
        ))}
      </ul>
      <p>Tổng: {cartItems.reduce((total, item) => total + item.price, 0)} VNĐ</p>
    </div>
  );
};

export default Cart;
