import axios from 'axios';

const handleSubmit = async (e) => {
  e.preventDefault();

  // Đảm bảo formData đã được định nghĩa và chứa dữ liệu cần thiết
  const formData = {
    username: username, // Thay thế bằng biến tương ứng
    email: email,       // Thay thế bằng biến tương ứng
    password: password, // Thay thế bằng biến tương ứng
  };

  try {
    const response = await axios.post('http://localhost:3000/api/auth/register', formData);
    console.log(response.data); // Xử lý phản hồi từ server
  } catch (error) {
    console.error('Lỗi trong quá trình đăng ký:', error.response?.data || error.message);
  }
};
