import React, { useState } from 'react';
import '../../css/productCard.css'; // Import CSS

const ProductCard = ({ frontImage, backImage, description, onClick }) => {
  const [isFlipped, setIsFlipped] = useState(false);

  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <div className={`product-card ${isFlipped ? 'flipped' : ''}`} onClick={handleFlip}>
      <div className="product-inner">
        <div className="product-front">
          <img src={frontImage} alt="Sản phẩm mặt trước" />
        </div>
        <div className="product-back">
          <img src={backImage} alt="Sản phẩm mặt sau" />
         
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
