// server.js hoặc app.js

const express = require('express');
const app = express();
const PORT = 3000;

// Middleware để phân tích cú pháp các yêu cầu JSON
app.use(express.json());

// Định nghĩa route /api/login
app.post('/api/login', (req, res) => {
  const { username, password } = req.body;

  // Kiểm tra thông tin đăng nhập đơn giản (chỉ để demo)
  if (username === 'admin' && password === 'password') {
    res.status(200).json({ message: 'Đăng nhập thành công', token: 'fake-jwt-token' });
  } else {
    res.status(401).json({ message: 'Thông tin đăng nhập không hợp lệ' });
  }
});

// Khởi động server
app.listen(PORT, () => {
  console.log(`Server đang chạy tại http://localhost:${PORT}`);
});
