import React, { useState, useRef, useEffect } from 'react'; // Import useState, useRef v√† useEffect
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // ƒê·∫£m b·∫£o r·∫±ng b·∫°n ƒë√£ c√†i ƒë·∫∑t axios
import '../../css/login.css';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [errorMessage, setErrorMessage] = useState(''); 
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Tr·∫°ng th√°i ƒë·ªÉ ki·ªÉm so√°t hi·ªÉn th·ªã icon kh√°ch h√†ng
  const passwordRef = useRef(null); // T·∫°o ref ƒë·ªÉ thao t√°c v·ªõi input
  const navigate = useNavigate(); // S·ª≠ d·ª•ng useNavigate

  const removeFocus = () => {
    if (passwordRef.current) {
      passwordRef.current.blur(); // Lo·∫°i b·ªè focus kh·ªèi √¥ nh·∫≠p m·∫≠t kh·∫©u
    }
  };

  // L∆∞u token khi ƒëƒÉng nh·∫≠p th√†nh c√¥ng v√† chuy·ªÉn h∆∞·ªõng ng∆∞·ªùi d√πng ƒë√£ ƒëƒÉng nh·∫≠p
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsLoggedIn(true); 
      navigate('/'); // Redirect t·ªõi trang ch·ªß n·∫øu ng∆∞·ªùi d√πng ƒë√£ ƒëƒÉng nh·∫≠p
    }
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/auth/login', {
        email: username, // Thay ƒë·ªïi username th√†nh email
        password: password,
      });
      
      // L∆∞u token v√† chuy·ªÉn h∆∞·ªõng n·∫øu ƒëƒÉng nh·∫≠p th√†nh c√¥ng
      localStorage.setItem('token', response.data.token);
       // T·∫£i l·∫°i trang sau khi ƒëƒÉng nh·∫≠p th√†nh c√¥ng
    window.location.reload();
      setIsLoggedIn(true); 
      navigate('/'); // Thay ƒë·ªïi ƒë∆∞·ªùng d·∫´n ƒë·∫øn trang ch·ªß n·∫øu c·∫ßn
    } catch (error) {
      if (error.response) {
        // L·ªói t·ª´ server
        if (error.response.status === 400) {
          setErrorMessage(error.response.data.message);
        } else {
          alert('L·ªói trong qu√° tr√¨nh ƒëƒÉng nh·∫≠p: ' + error.response.data.message);
        }
      } else if (error.request) {
        alert('Kh√¥ng nh·∫≠n ƒë∆∞·ª£c ph·∫£n h·ªìi t·ª´ server');
      } else {
        alert('L·ªói: ' + error.message);
      }
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Classy Login Form</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-box">
            <span className="icon">&#128100;</span>
            <input
              type="text"
              placeholder="Email"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="input-box">
            <span className="icon">&#128274;</span>
            <input
              type={showPassword ? 'text' : 'password'} // Thay ƒë·ªïi lo·∫°i input
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              ref={passwordRef}
              required
            />
            <span
              className="icon-password"
              onClick={() => {
                setShowPassword(!showPassword);
                removeFocus(); // B·ªè focus khi click v√†o bi·ªÉu t∆∞·ª£ng
              }}
            >
              {showPassword ? 'üëÅÔ∏è' : 'üôà'} {/* Bi·ªÉu t∆∞·ª£ng ƒë·ªÉ hi·ªÉn th·ªã ho·∫∑c ·∫©n m·∫≠t kh·∫©u */}
            </span>
          </div>
          {errorMessage && <div className="error-message">{errorMessage}</div>} {/* Hi·ªÉn th·ªã th√¥ng b√°o l·ªói */}
          <div className="options">
            <label>
              <input type="checkbox" /> Remember me
            </label>
            <a href="#">Forgot Password?</a>
          </div>
          <button type="submit" className="login-btn">Login</button>
          <div className="or">or</div>
          <div className="social-login">
            <button className="facebook" type="button">Facebook</button>
            <button className="twitter" type="button">Twitter</button>
          </div>
          <div className="register-link">
            <button type="button" onClick={() => navigate('/register')}>Register</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
