import React from 'react';
import { Link } from 'react-router-dom';
import '../../css/productList.css';

const ProductList = () => {
  const handlePrevClick = () => {
    document.querySelector('.product-list').scrollBy({
      left: -200, // Cuộn ngược lại 200px
      behavior: 'smooth',
    });
  };

  const handleNextClick = () => {
    document.querySelector('.product-list').scrollBy({
      left: 200, // Cuộn tới 200px
      behavior: 'smooth',
    });
  };

  // Mảng sản phẩm tĩnh
  const products = [
    { id: 1, name: 'Áo thun', price: 200000, image: 'https://ithk-pro-itmall-item.oss-cn-hongkong.aliyuncs.com/2/product/0ZXTEM110323NWHX/0ZXTEM110323NWHX-pdp-1.jpg' },
    { id: 2, name: 'Quần jeans', price: 500000, image: 'https://ithk-pro-itmall-item.oss-cn-hongkong.aliyuncs.com/2/product/0ZXSWM115304NGRX/0ZXSWM115304NGRX-pdp-1.jpg' },
    { id: 3, name: 'Áo khoác', price: 700000, image: 'https://ithk-pro-itmall-item.oss-cn-hongkong.aliyuncs.com/2/product/0ZXLTM111015NGYX/0ZXLTM111015NGYX-pdp-1.jpg' },
    { id: 4, name: 'Cargo jean', price: 1000000, image: 'https://int.bape.com/cdn/shop/files/0PXJNW250302NBLX-pdp-1.jpg?v=1727433157&width=1200https://int.bape.com/cdn/shop/files/0PXJNW250302NBLX-pdp-2.jpg?v=1727433157&width=700' }
  ];

  return (
    <div className="products-section">
      <h2>New Arrivals</h2>
      <button className="arrow prev" onClick={handlePrevClick}>&#10094;</button>
      <div className="product-list">
        {products.map((product) => (
          <div key={product.id} className="product-item">
            <Link to={`/products/${product.id}`} className="product-link">
              <img src={product.image} alt={product.name} className="product-image" />
            </Link>
            <div className="product-info">
              <Link to={`/products/${product.id}`} className="product-link">
                <h3 className="product-name">{product.name}</h3>
              </Link>
              <p className="product-price">{product.price.toLocaleString()} VND</p>
            </div>
          </div>
        ))}
      </div>
      <button className="arrow next" onClick={handleNextClick}>&#10095;</button>
    </div>
  );
};

export default ProductList;
