import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/home';
import Login from './components/login';
import Register from './components/register';
import Navbar from './components/user/navbar';
import ProductList from './components/user/productList';
import ProductDetail from './components/user/productDetail';
import Cart from './components/user/cart';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';

const App = () => {
  const [selectedProduct, setSelectedProduct] = useState(null);

  const products = [
    { id: 1, name: 'Áo thun', price: 200000, image: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/638715d6-c593-411a-aedd-7bea41da9d7e/M+NSW+CLUB+TEE.png' },
    { id: 2, name: 'Quần jeans', price: 500000, image: 'https://example.com/jeans.jpg' },
    { id: 3, name: 'Áo khoác', price: 700000, image: 'https://example.com/jacket.jpg' }
  ];

  const addToCart = (product) => {
    setCartItems([...cartItems, product]);
  };

  const viewProductDetail = (product) => {
    setSelectedProduct(product);
  };

  const backToList = () => {
    setSelectedProduct(null);
  };

  return (
    <Router>
      <Navbar cartCount={cartItems.length} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route
          path="/products"
          element={
            selectedProduct ? (
              <ProductDetail product={selectedProduct} addToCart={addToCart} onBack={backToList} />
            ) : (
              <ProductList products={products} onViewDetail={viewProductDetail} />
            )
          }
        />
      </Routes>
      <Cart cartItems={cartItems} />
    </Router>
  );
  const cartItems = [
    {
      id: 1,
      name: 'Sản phẩm 1',
      image: 'https://via.placeholder.com/50',
      price: 29.99,
      quantity: 2,
    },
    {
      id: 2,
      name: 'Sản phẩm 2',
      image: 'https://via.placeholder.com/50',
      price: 15.99,
      quantity: 1,
    },
  ];
};

export default App;
