import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './css/admin.css';
import '../../css/navbar.css';

const AdminPage = () => {
  const [adminData, setAdminData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');

  // Hàm để fetch dữ liệu admin
  const fetchAdminData = async () => {
    setLoading(true); // Bắt đầu loading
    setErrorMessage(''); // Reset thông báo lỗi
    try {
      const response = await axios.get('http://localhost:3000/api/auth/admin', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}` // Gửi token trong header
        }
      });
      setAdminData(response.data); // Lưu dữ liệu từ response
    } catch (error) {
      if (error.response) {
        switch (error.response.status) {
          case 401:
            setErrorMessage('Bạn không có quyền truy cập. Vui lòng đăng nhập.');
            break;
          case 403:
            setErrorMessage('Quyền truy cập bị từ chối.');
            break;
          case 404:
            setErrorMessage('Không tìm thấy dữ liệu.');
            break;
          default:
            setErrorMessage(error.response.data.message || 'Đã xảy ra lỗi.');
        }
      } else {
        setErrorMessage('Lỗi trong quá trình tải dữ liệu');
      }
    } finally {
      setLoading(false); // Kết thúc loading
    }
  };

  useEffect(() => {
    fetchAdminData(); // Gọi hàm khi component được mount
  }, []);

  return (
    <div className="admin-container">
      <h1>Admin Page</h1>
      <nav className="admin-menu">
        <ul>
          <li><Link to="/admin/add-product">Thêm Sản Phẩm</Link></li>
          <li><Link to="/admin/manage-customers">Quản Lý Khách Hàng</Link></li>
          <li><Link to="/admin/manage-orders">Quản Lý Đơn Hàng</Link></li>
        </ul>
      </nav>
      {loading ? (
        <div className="loading-spinner">Loading...</div>
      ) : errorMessage ? (
        <div className="error-message">
          {errorMessage}
          <button onClick={fetchAdminData}>Tải lại</button>
        </div>
      ) : (
        <div>
          {adminData ? (
            Array.isArray(adminData) ? (
              <div>
                {adminData.map((item, index) => (
                  <div key={index}>
                    <p>{JSON.stringify(item)}</p>
                  </div>
                ))}
              </div>
            ) : (
              <div>
                <pre>{JSON.stringify(adminData, null, 2)}</pre>
              </div>
            )
          ) : (
            <div>No data available</div>
          )}
        </div>
      )}
    </div>
  );
};

export default AdminPage;
