import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Home from './components/home';
import Login from './components/login';
import Register from './components/register';
import Navbar from './components/user/navbar';
import ProductList from './components/user/productList';
import ProductDetail from './components/user/productDetail';
import Cart from './components/user/cart';
import VideoPlayer from './components/video';
import ProductCard from './components/user/productCard'; // Import ProductCard component

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const products = [
    {
      frontImage: '/img/0ZXTEM110049NWHX-pdp-1.jpg',
      backImage: '/img/0ZXTEM110049NWHX-pdp-2.jpg',
      description: 'Mô tả sản phẩm 1',
    },
    {
      frontImage: '/img/another-product-front.jpg',
      backImage: '/img/another-product-back.jpg',
      description: 'Mô tả sản phẩm 2',
    },
    // Thêm sản phẩm khác nếu cần
  ];
  return (
    <div className="home">
      {products.map((product, index) => (
        <ProductCard
          key={index}
          frontImage={product.frontImage}
          backImage={product.backImage}
          description={product.description}
        />
      ))}
    </div>
  );

  const addToCart = (product) => {
    setCartItems((prevCartItems) => {
      const existingProductIndex = prevCartItems.findIndex(item => item.id === product.id);
      if (existingProductIndex !== -1) {
        const updatedCartItems = [...prevCartItems];
        updatedCartItems[existingProductIndex].quantity += 1;
        return updatedCartItems;
      } else {
        return [...prevCartItems, { ...product, quantity: 1 }];
      }
    });
  };

  return (
    <Router>
      <Navbar cartCount={cartItems.length} />
      <Routes>
        <Route
          path="/"
          element={<Home products={products} />}
        />
        <Route
          path="/login"
          element={<Login />}
        />
        <Route
          path="/register"
          element={<Register />}
        />
        <Route
          path="/products/:id"
          element={<ProductDetail products={products} addToCart={addToCart} />}
        />
        <Route
          path="/cart"
          element={<Cart cartItems={cartItems} onRemove={(id) => setCartItems(cartItems.filter(item => item.id !== id))} />}
        />
        <Route
          path="/video"
          element={
            <div>
              <h1>My Video</h1>
              <VideoPlayer />
            </div>
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
