import React from 'react';
import { Link } from 'react-router-dom';
import PromotionBanner from './components/user/PromotionBanner';
import '../css/home.css'; // Thêm CSS cho trang chủ
import HomeVideo from './video'; // Import component video
import ProductList from './user/productList';

const Home = ({ products }) => {
const promoMessage = "Khuyến mãi: Giảm 20% cho đơn hàng đầu tiên!";
  return (
    <div className="home-container">
      <h1>Trang Chủ</h1>
      
    <div className="home">
      <PromotionBanner message={promoMessage} />
      <h1>Chào mừng đến với cửa hàng của chúng tôi!</h1>
      {/* Các phần khác của trang chính */}

    </div>
      {/* Chèn video vào trang chính */}
      <div className="video-container">
        <HomeVideo />
      </div>
      
      {/* Danh sách sản phẩm */}
      <div className="productList_container">
        <ProductList />
      </div>
  
    </div>
  );
};

export default Home;
