import React, { useState } from 'react';
import '../../css/login.css';
import { useHistory } from 'react-router-dom'; // Import useHistory để điều hướng

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const history = useHistory(); // Khởi tạo useHistory

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await fetch('http://localhost:3000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Đã xảy ra lỗi');
      }

      alert(data.message); // Hoặc xử lý thông tin người dùng ở đây
    } catch (error) {
      setError(error.message);
    }
  };

  const handleRegister = () => {
    // Điều hướng đến trang đăng ký
    history.push('/register'); // Đảm bảo rằng bạn đã thiết lập router cho trang đăng ký
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Classy Login Form</h2>
        {error && <div className="error-message">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="input-box">
            <span className="icon">&#128100;</span>
            <input
              type="text"
              placeholder="User name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="input-box">
            <span className="icon">&#128274;</span>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="options">
            <label>
              <input type="checkbox" /> Remember me
            </label>
            <a href="#">Forgot Password?</a>
          </div>
          <button type="submit" className="login-btn">Login</button>
          <div className="or">or</div>
          <div className="social-login">
            <button className="facebook" type="button">Facebook</button>
            <button className="twitter" type="button">Twitter</button>
          </div>
          <button type="button" className="register-btn" onClick={handleRegister}>
            Register
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
