import React from 'react';

const Cart = ({ cartItems }) => {
  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>
      <ul>
        {cartItems.map((item, index) => (
          <li key={index}>
            {item.name} - {item.price} VND - Kích thước: {item.size}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Cart;
