// src/components/Home.js
import React from 'react';
import SearchBar from '../components/searchBar'; // Nhập SearchBar
import '../css/home.css';

const Home = () => {
  return (
    <div>
      <h1>Chào mừng đến với Cửa hàng quần áo</h1>
      <SearchBar /> {/* Thêm thanh tìm kiếm */}
      <p>Khám phá các sản phẩm thời trang mới nhất và mua sắm các món đồ yêu thích của bạn.</p>
      <img 
        src="" 
        alt="Chào mừng" 
        style={{ width: '100%', height: 'auto' }} 
      />
    </div>
  );
};

export default Home;
