import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/home';
import Register from './components/register';
import Navbar from './components/user/navbar';
import ProductDetail from './components/user/productDetail';
import Cart from './components/user/cart';
import VideoPlayer from './components/video';
import UserGroups from './components/user/usergroups';
import MenProductList from './components/user/MenProductList';
import WomenProductList from './components/user/WomenProductList';
import KidProductList from './components/user/KidProductList';
import LoginForm from './components/user/LoginForm';
import AdminPage from './components/admin/AdminPage'; 

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [role, setRole] = useState(null); // Khai báo trạng thái cho role
  const ProtectedRoute = ({ children, isLoggedIn, role }) => {
    if (!isLoggedIn) {
      return <Navigate to="/login" />;
    }
  
    if (role !== 'admin') {
      return <Navigate to="/" />; // Chuyển hướng người dùng không phải admin
    }
  
    return children;
  };

  // Kiểm tra token trong localStorage khi ứng dụng khởi chạy
  useEffect(() => {
    const token = localStorage.getItem('token');
    const userRole = localStorage.getItem('role');
    if (token) {
      setIsLoggedIn(true);
      setRole(userRole); // Cập nhật vai trò
    }
  }, []);

  const products = [
    // Dữ liệu sản phẩm của bạn
  ];

  return (
    <div>
      <Navbar cartCount={cartItems.length} isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} role={role}  /> {/* Truyền setIsLoggedIn vào Navbar */}
      <Routes>
        <Route path="/" element={<Home products={products} />} />
        <Route path="/login" element={<LoginForm setIsLoggedIn={setIsLoggedIn} setRole={setRole} />} /> {/* Truyền setIsLoggedIn vào LoginForm */}
        <Route path="/register" element={<Register />} />
        <Route path="/products/:id" element={<ProductDetail products={products} />} />
        <Route path="/cart" element={<Cart cartItems={cartItems} />} />
        <Route path="/video" element={<VideoPlayer />} />
        <Route path="/user-groups" element={<UserGroups />} />
        <Route path="/products/men" element={<MenProductList />} />
        <Route path="/products/women" element={<WomenProductList />} />
        <Route path="/products/kid" element={<KidProductList />} />
        <Route path="/admin" element={
        <ProtectedRoute isLoggedIn={isLoggedIn} role={role}>
        <AdminPage />
        </ProtectedRoute>
             } />
      </Routes>
    </div>
  );
};

const AppWrapper = () => (
  <Router>
    <App />
  </Router>
);

export default AppWrapper;
