// src/components/AdminAddProduct.js
import React, { useState } from 'react';

const AdminAddProduct = () => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState('');
  const [sizes, setSizes] = useState(['M']); // Mặc định có một kích thước
  const [message, setMessage] = useState('');

  const handleAddProduct = async (e) => {
    e.preventDefault();

    const productData = { name, price: Number(price), image, sizes };

    try {
      const response = await fetch('http://localhost:3000/api/auth/admin/products', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`, // Gửi token
        },
        body: JSON.stringify(productData),
      });

      if (response.ok) {
        const data = await response.json();
        setMessage(data.message);
        // Reset form
        setName('');
        setPrice('');
        setImage('');
        setSizes(['M']);
      } else {
        const errorData = await response.json();
        setMessage(`Error: ${errorData.message}`);
      }
    } catch (error) {
      console.error('Error adding product:', error);
      setMessage('Có lỗi xảy ra khi thêm sản phẩm');
    }
  };

  return (
    <form onSubmit={handleAddProduct}>
      <div>
        <label>Tên sản phẩm:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
      </div>
      <div>
        <label>Giá sản phẩm:</label>
        <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} required />
      </div>
      <div>
        <label>Hình ảnh sản phẩm:</label>
        <input type="text" value={image} onChange={(e) => setImage(e.target.value)} required />
      </div>
      <div>
        <label>Kích thước:</label>
        <input
          type="text"
          value={sizes.join(',')}
          onChange={(e) => setSizes(e.target.value.split(',').map(size => size.trim()))}
          required
        />
      </div>
      <button type="submit">Thêm sản phẩm</button>
      {message && <p>{message}</p>} {/* Hiển thị thông báo */}
    </form>
  );
};

export default AdminAddProduct;
