import React, { useState, useRef } from 'react';
import ClothingOptions from './ClothingOptions';
import '../../css/style.css';

const VirtualTryOn = () => {
  const [userImage, setUserImage] = useState(null);
  const [clothingImage, setClothingImage] = useState(null);
  const canvasRef = useRef(null);

  // Xử lý khi người dùng tải ảnh lên
  const handleImageUpload = (e) => {
    const reader = new FileReader();
    reader.onload = () => {
      setUserImage(reader.result);
    };
    reader.readAsDataURL(e.target.files[0]);
  };

  // Xử lý khi chọn quần áo
  const handleClothingSelection = (clothing) => {
    setClothingImage(clothing);
  };

  // Vẽ ảnh lên canvas
  const drawCanvas = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Vẽ ảnh người dùng
    if (userImage) {
      const img = new Image();
      img.src = userImage;
      img.onload = () => {
        ctx.drawImage(img, 0, 0, canvas.width, canvas.height);
      };
    }

    // Vẽ quần áo
    if (clothingImage) {
      const img = new Image();
      img.src = clothingImage;
      img.onload = () => {
        ctx.drawImage(img, 100, 200, 200, 200); // Vị trí và kích thước của quần áo
      };
    }
  };

  // Khi userImage hoặc clothingImage thay đổi, vẽ lại canvas
  React.useEffect(() => {
    drawCanvas();
  }, [userImage, clothingImage]);

  return (
    <div className="App">
      <h1>Thử đồ trực tuyến</h1>
      <input type="file" accept="image/*" onChange={handleImageUpload} />
      <canvas ref={canvasRef} width={400} height={600} className="canvas" />
      <ClothingOptions onClothingSelect={handleClothingSelection} />
    </div>
  );
};

export default VirtualTryOn;
