import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/home';
import Login from './components/login';
import Register from './components/register';
import Navbar from './components/user/navbar';
import ProductList from './components/user/productList';
import ProductDetail from './components//user/productDetail';
import Cart from './components/user/cart';


const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
      </Routes>
    </Router>
  );
  const [cartItems, setCartItems] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const products = [
    { id: 1, name: 'Áo thun', price: 200000, image: 'https://example.com/tshirt.jpg' },
    { id: 2, name: 'Quần jeans', price: 500000, image: 'https://example.com/jeans.jpg' },
    { id: 3, name: 'Áo khoác', price: 700000, image: 'https://example.com/jacket.jpg' }
  ];

  const addToCart = (product) => {
    setCartItems([...cartItems, product]);
  };

  const viewProductDetail = (product) => {
    setSelectedProduct(product);
  };

  const backToList = () => {
    setSelectedProduct(null);
  };

  return (
    <div>
      <Navbar cartCount={cartItems.length} />
      {selectedProduct ? (
        <ProductDetail product={selectedProduct} addToCart={addToCart} onBack={backToList} />
      ) : (
        <ProductList products={products} onViewDetail={viewProductDetail} />
      )}
      <Cart cartItems={cartItems} />
    </div>
  );
};


export default App;
