import React from 'react';
import VideoPlayer from './video';
import '../css/home.css'; // CSS cho trang chủ

const Home = ({ products }) => {
  return (
    <div className="home-container">
      <div className="navbar">
        {/* Navbar content here */}
      </div>
      
      <div className="video-container">
        <VideoPlayer /> {/* Chèn video vào trang chính */}
      </div>
      
      <div className="home-content">
        <div className="product-list">
          {products.map((product) => (
            <ProductCard
              key={product.id}
              frontImage={product.image}
              backImage="https://example.com/back-image.jpg" // Thay thế bằng đường dẫn hình ảnh mặt sau nếu có
              description={`Tên sản phẩm: ${product.name}`} // Cung cấp mô tả sản phẩm
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
