import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../../css/productDetail.css';

const ProductDetail = ({ products, addToCart }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [selectedSize, setSelectedSize] = useState('M'); // Trạng thái lưu kích thước

  const product = products.find(p => p.id === parseInt(id));

  if (!product) {
    return <div>Product not found</div>;
  }

  const handleAddToCart = () => {
    addToCart({ ...product, size: selectedSize }); // Thêm kích thước vào sản phẩm khi thêm vào giỏ
  };

  const handleSizeChange = (e) => {
    setSelectedSize(e.target.value); // Cập nhật kích thước khi người dùng chọn
  };

  return (
    <div className="product-detail">
      <button onClick={() => navigate('/')}>Trở về danh sách</button>
      <img src={product.image} alt={product.name} />
      <h2>{product.name}</h2>
      <p>Giá: {product.price.toLocaleString()} VND</p>
      <label>Kích thước:</label>
      <select value={selectedSize} onChange={handleSizeChange}> {/* Gán sự kiện onChange */}
        <option value="S">S</option>
        <option value="M">M</option>
        <option value="L">L</option>
        <option value="XL">XL</option>
      </select>
      <button onClick={handleAddToCart}>Thêm vào giỏ</button>
    </div>
  );
};

export default ProductDetail;
