import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/home';
import Login from './components/login';
import Register from './components/register';
import Navbar from './components/user/navbar';
import ProductDetail from './components/user/productDetail';
import Cart from './components/user/cart';
import VideoPlayer from './components/video';
import MenProductList from './components/user/MenProductList'; // Import MenProductList
import UserGroups from '../user/UserGroups'; // Import UserGroups
import ProductList from './user/ProductList'; // Import ProductList

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (product) => {
    setCartItems((prevCartItems) => {
      const existingProductIndex = prevCartItems.findIndex(item => item.id === product.id);
      if (existingProductIndex !== -1) {
        const updatedCartItems = [...prevCartItems];
        updatedCartItems[existingProductIndex].quantity += 1;
        return updatedCartItems;
      } else {
        return [...prevCartItems, { ...product, quantity: 1 }];
      }
    });
  };

  return (
    <div>
      <Navbar cartCount={cartItems.length} />
      <Routes>
        <Route path="/" element={<UserGroups />} />
        <Route path="/products/men" element={<MenProductList />} /> {/* Thêm route cho MenProductList */}
        <Route path="/products" element={<ProductList />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/products/:id" element={<ProductDetail addToCart={addToCart} />} />
        <Route path="/cart" element={<Cart cartItems={cartItems} onRemove={(id) => setCartItems(cartItems.filter(item => item.id !== id))} />} />
        <Route path="/video" element={<VideoPlayer />} />
      </Routes>
    </div>
  );
};

const AppWrapper = () => (
  <Router>
    <App />
  </Router>
);

export default AppWrapper;
