import React from 'react';
import { Link } from 'react-router-dom';
import '../css/home.css'; // Thêm CSS cho trang chủ
import HomeVideo from './video'; // Import component video

const Home = ({ products }) => {
const promoMessage = "Khuyến mãi: Giảm 20% cho đơn hàng đầu tiên!";
  return (
    <div className="home-container">
      <h1>Trang Chủ</h1>
      
    

  return (
    <div className="home">
      <PromotionBanner message={promoMessage} />
      <h1>Chào mừng đến với cửa hàng của chúng tôi!</h1>
      {/* Các phần khác của trang chính */}
    </div>
  );
      {/* Chèn video vào trang chính */}
      <div className="video-container">
        <HomeVideo />
      </div>
      
      {/* Danh sách sản phẩm */}
      <div className="products-section">
        <h2>Sản phẩm</h2>
        <div className="product-list">
          {products.map((product) => (
            <div key={product.id} className="product-item">
              <Link to={`/products/${product.id}`} className="product-link">
                <img src={product.image} alt={product.name} className="product-image" />
              </Link>
              <div className="product-info">
                <h3 className="product-name">{product.name}</h3>
                <p className="product-price">{product.price.toLocaleString()} VND</p>
              </div>
            </div>
          ))}
        </div>
      </div>

    
    </div>
  );
};

export default Home;
