import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Home from './components/home';
import Login from './components/login';
import Register from './components/register';
import Navbar from './components/user/navbar';
import ProductList from './components/user/productList';
import ProductDetail from './components/user/productDetail';
import Cart from './components/user/cart';
import VideoPlayer from './components/video';

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const navigate = useNavigate();

  const products = [
    { id: 1, name: 'Áo thun', price: 200000, image: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/638715d6-c593-411a-aedd-7bea41da9d7e/M+NSW+CLUB+TEE.png' },
    { id: 2, name: 'Quần jeans', price: 500000, image: 'https://example.com/jeans.jpg' },
    { id: 3, name: 'Áo khoác', price: 700000, image: 'https://example.com/jacket.jpg' }
  ];

  const addToCart = (product) => {
    setCartItems((prevCartItems) => {
      const existingProductIndex = prevCartItems.findIndex(item => item.id === product.id);
      if (existingProductIndex !== -1) {
        const updatedCartItems = [...prevCartItems];
        updatedCartItems[existingProductIndex].quantity += 1;
        return updatedCartItems;
      } else {
        return [...prevCartItems, { ...product, quantity: 1 }];
      }
    });
  };

  return (
    <Router>
      <Navbar cartCount={cartItems.length} />
      <Routes>
        <Route path="/" element={<Home products={products} onViewDetail={(product) => navigate(`/products/${product.id}`)} />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/products/:id" element={<ProductDetail products={products} addToCart={addToCart} />} />
        <Route path="/cart" element={<Cart cartItems={cartItems} onRemove={(id) => setCartItems(cartItems.filter(item => item.id !== id))} />} />
        {/* Thêm route cho video */}
        <Route path="/video" element={
          <div>
            <h1>My Video</h1>
            <VideoPlayer />
          </div>
        } />
      </Routes>
    </Router>
  );
};

export default App;
