import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/home';
import Login from './components/login';
import Register from './components/register';
import Navbar from './components/user/navbar';
import ProductList from './components/user/productList';
import ProductDetail from './components/user/productDetail';
import Cart from './components/user/cart';

const App = () => {
  const [cartItems, setCartItems] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [isCartVisible, setIsCartVisible] = useState(false);

  const products = [
    { id: 1, name: 'Áo thun', price: 200000, image: 'https://static.nike.com/a/images/c_limit,w_592,f_auto/t_product_v1/638715d6-c593-411a-aedd-7bea41da9d7e/M+NSW+CLUB+TEE.png' },
    { id: 2, name: 'Quần jeans', price: 500000, image: 'https://example.com/jeans.jpg' },
    { id: 3, name: 'Áo khoác', price: 700000, image: 'https://example.com/jacket.jpg' }
  ];

  const addToCart = (product) => {
    setCartItems((prevCartItems) => {
      const existingProductIndex = prevCartItems.findIndex(item => item.id === product.id);
      if (existingProductIndex !== -1) {
        // Nếu sản phẩm đã có trong giỏ hàng, chỉ cần cập nhật số lượng
        const updatedCartItems = [...prevCartItems];
        updatedCartItems[existingProductIndex].quantity += 1;
        return updatedCartItems;
      } else {
        // Nếu sản phẩm chưa có trong giỏ hàng, thêm mới vào giỏ
        return [...prevCartItems, { ...product, quantity: 1 }];
      }
    });
  };

  const viewProductDetail = (product) => {
    setSelectedProduct(product);
  };

  const backToList = () => {
    setSelectedProduct(null);
  };

  const toggleCartVisibility = () => {
    setIsCartVisible(!isCartVisible);
  };

  const handleRemoveItem = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  return (
    <Router>
      <Navbar cartCount={cartItems.length} onCartClick={toggleCartVisibility} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route
          path="/products"
          element={
            selectedProduct ? (
              <ProductDetail product={selectedProduct} addToCart={addToCart} onBack={backToList} />
            ) : (
              <ProductList products={products} onViewDetail={viewProductDetail} />
            )
          }
        />
      </Routes>
      {isCartVisible && (
        <Cart
          cartItems={cartItems}
          onClose={() => setIsCartVisible(false)}
          onRemove={handleRemoveItem}
        />
      )}
    </Router>
  );
};

export default App;
