// src/components/user/MenProductList.js
import React from 'react';
import { Link } from 'react-router-dom';

const menProducts = [
  { id: 1, name: 'STA Houndstooth Baby Milo Tee', price: 129000, image: 'https://example.com/path/to/image1.jpg' },
  { id: 2, name: 'STA Houndstooth College Relaxed Fit Tee', price: 147000, image: 'https://example.com/path/to/image2.jpg' },
  { id: 3, name: 'STA Houndstooth By Bathing Ape Relaxed Fit Tee', price: 147000, image: 'https://example.com/path/to/image3.jpg' },
  { id: 4, name: 'STA Houndstooth Ape Head Tee', price: 129000, image: 'https://example.com/path/to/image4.jpg' },
];

const MenProductList = () => {
  return (
    <div>
      <h1>Men's Products</h1>
      <div className="product-list">
        {menProducts.map(product => (
          <div key={product.id} className="product-item">
            <Link to={`/products/${product.id}`}>
              <img src={product.image} alt={product.name} className="product-image" />
            </Link>
            <h3 className="product-name">{product.name}</h3>
            <p className="product-price">{product.price.toLocaleString()} VND</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MenProductList;
