import React, { useState,useRef } from 'react'; // Import useState và useRef
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Đảm bảo rằng bạn đã cài đặt axios
import '../../css/login.css';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [errorMessage, setErrorMessage] = useState(''); 
  const [error, setError] = useState(''); // Thêm trạng thái lỗi
  const passwordRef = useRef(null); // Tạo ref để thao tác với input
  const navigate = useNavigate(); // Sử dụng useNavigate
  const removeFocus = () => {
    if (passwordRef.current) {
      passwordRef.current.blur(); // Loại bỏ focus khỏi ô nhập mật khẩu
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/auth/login', {
        email: username, // Thay đổi username thành email
        password: password,
      });
      alert(response.data.message);
      // Chuyển hướng nếu đăng nhập thành công
      navigate('/'); // Thay đổi đường dẫn đến trang chủ nếu cần
    } catch (error) {
      if (error.response) {
        // Lỗi từ server
        if (error.response.status === 400) {
          setErrorMessage(error.response.data.message);
        } else {
          alert('Lỗi trong quá trình đăng nhập: ' + error.response.data.message);
        }
      } else if (error.request) {
        alert('Không nhận được phản hồi từ server');
      } else {
        alert('Lỗi: ' + error.message);
      }
    }
  };
  

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Classy Login Form</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-box">
            <span className="icon">&#128100;</span>
            <input
              type="text"
              placeholder="Email"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="input-box">
            <span className="icon">&#128274;</span>
            <input
            type={showPassword ? 'text' : 'password'} // Thay đổi loại input
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
               <span
              onClick={() => {
                setShowPassword(!showPassword)
                removeFocus(); // Bỏ focus khi click vào biểu tượng
            }} 
           
            >
              {showPassword ? '👁️' : '🙈'} {/* Biểu tượng để hiển thị hoặc ẩn mật khẩu */}
            </span>
          </div>
          {error && <div className="error-message">{error}</div>} {/* Hiển thị thông báo lỗi */}
          <div className="options">
            <label>
              <input type="checkbox" /> Remember me
            </label>
            <a href="#">Forgot Password?</a>
          </div>
          <button type="submit" className="login-btn">Login</button>
          <div className="or">or</div>
          <div className="social-login">
            <button className="facebook" type="button">Facebook</button>
            <button className="twitter" type="button">Twitter</button>
          </div>
          <div className="register-link">
            <button type="button" onClick={() => navigate('/register')}>Register</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
