import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../../css/login.css';

const LoginForm = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false); // State để theo dõi hiển thị mật khẩu
  const navigate = useNavigate(); // Sử dụng useNavigate

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Username:', username);
    console.log('Password:', password);
    // Thực hiện logic xử lý đăng nhập tại đây
    // Sau khi xử lý thành công, bạn có thể điều hướng
    // navigate('/desired-path'); // Thay đổi đường dẫn đến trang bạn muốn điều hướng
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Classy Login Form</h2>
        <form onSubmit={handleSubmit}>
          <div className="input-box">
            <span className="icon">&#128100;</span>
            <input
              type="text"
              placeholder="User name"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="input-box">
            <span className="icon">&#128274;</span>
            <input
              type={showPassword ? 'text' : 'password'} // Thay đổi loại input
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <label>
              <input 
                type="checkbox" 
                checked={showPassword} 
                onChange={() => setShowPassword(!showPassword)} 
              />
              Hiển thị mật khẩu
            </label>
          </div>
          <div className="options">
            <label>
              <input type="checkbox" /> Remember me
            </label>
            <a href="#">Forgot Password?</a>
          </div>
          <button type="submit" className="login-btn">Login</button>
          <div className="or">or</div>
          <div className="social-login">
            <button className="facebook" type="button">Facebook</button>
            <button className="twitter" type="button">Twitter</button>
          </div>
          <div className="register-link">
            <button type="button" onClick={() => navigate('/register')}>Register</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
