import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../../css/productDetail.css';

const ProductDetail = ({ products, addToCart }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [selectedSize, setSelectedSize] = useState('M'); // Trạng thái lưu kích thước
  const [notification, setNotification] = useState(null);  // Trạng thái thông báo

  const product = products.find(p => p.id === Number(id));

  if (!product) {
    return <div>Product not found</div>;
  }

  const handleAddToCart = () => {
    const cartProduct = { ...product, size: selectedSize };
    addToCart(cartProduct); // Thêm sản phẩm vào giỏ hàng
    showNotification(cartProduct); // Hiển thị thông báo với thông tin sản phẩm
  };

  // Hàm hiển thị thông báo
  const showNotification = (product) => {
    setNotification(product);
    setTimeout(() => {
      setNotification(null); // Ẩn thông báo sau 3 giây
    }, 3000);
  };

  const handleSizeChange = (e) => {
    setSelectedSize(e.target.value);
  };

  return (
    <div className="product-detail">
      <button onClick={() => navigate('/')}>Trở về danh sách</button>
      <div className='product-content'>
        <div className='product-images'>
          <img src={product.image} alt={product.name} className="main-image" />
          <div className='thumbnail-images'>
            {product.thumbnailImages?.map((thumb, index) => (
              <img key={index} src={thumb} alt={`thumbnail-${index}`} className="thumbnail" />
            )) ?? <p>Không có ảnh thu nhỏ</p>}
          </div>
        </div>
        
        <div className='product-info'>
          <h2>{product.name}</h2>
          <p>Giá: {product.price.toLocaleString()} VND</p>
          <label htmlFor="size-select">Kích thước:</label>
          <select id="size-select" value={selectedSize} onChange={handleSizeChange}>
            {product.sizes?.map((size, index) => (
              <option key={index} value={size}>{size}</option>
            )) ?? <option value="">Không có kích thước</option>}
          </select>
          <button onClick={handleAddToCart}>Thêm vào giỏ</button>
        </div>
      </div>
      
      {notification && (
        <div className="notification">
          <img src={notification.image} alt={notification.name} className="notification-img" />
          <div className="notification-info">
            <h3>{notification.name}</h3>
            <p>Kích thước: {notification.size}</p>
            <p>Giá: {notification.price.toLocaleString()} VND</p>
          </div>
          <button className="view-cart-button" onClick={() => navigate('/cart')}>Xem giỏ hàng</button>
        </div>
      )}
    </div>
  );
  
};

export default ProductDetail;
