import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminPage = () => {
  const [adminData, setAdminData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const fetchAdminData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/auth/admin', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}` // Gửi token trong header
          }
        });
        setAdminData(response.data); // Lưu dữ liệu từ response
      } catch (error) {
        if (error.response) {
          setErrorMessage(error.response.data.message);
        } else {
          setErrorMessage('Lỗi trong quá trình tải dữ liệu');
        }
      } finally {
        setLoading(false); // Kết thúc loading
      }
    };

    fetchAdminData(); // Gọi hàm khi component được mount
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Hiển thị loading nếu đang tải dữ liệu
  }

  if (errorMessage) {
    return <div className="error-message">{errorMessage}</div>; // Hiển thị thông báo lỗi nếu có
  }
  router.get('/admin', verifyToken, (req, res) => {
    // Logic xử lý cho admin
    res.status(200).json({ message: 'Đây là trang admin' });
});
  return (
    <div>
      <h1>Admin Page</h1>
      {adminData ? (
        <div>
          {/* Hiển thị dữ liệu của admin ở đây */}
          <pre>{JSON.stringify(adminData, null, 2)}</pre>
        </div>
      ) : (
        <div>No data available</div> // Nếu không có dữ liệu
      )}
    </div>
  );
};

export default AdminPage;
