import React from 'react';
import { Link } from 'react-router-dom';
import '../css/home.css'; // Thêm CSS cho trang chủ
import HomeVideo from './video'; // Import component video
import VirtualTryOn from './user/VirtualTryOn'; // Import component thử đồ

const Home = ({ products }) => {
  return (
    <div className="home-container">
      <h1>Trang Chủ</h1>
      
      {/* Chèn video vào trang chính */}
      <div className="video-container">
        <HomeVideo />
      </div>
      
      {/* Danh sách sản phẩm */}
      <div className="products-section">
        <h2>Sản phẩm</h2>
        <div className="product-list">
          {products.map((product) => (
            <div key={product.id} className="product-item">
              <Link to={`/products/${product.id}`} className="product-link">
                <img src={product.image} alt={product.name} className="product-image" />
              </Link>
              <div className="product-info">
                <h3 className="product-name">{product.name}</h3>
                <p className="product-price">{product.price.toLocaleString()} VND</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Thử đồ trực tuyến */}
      <div className="try-on-section">
        <h2>Thử đồ trực tuyến</h2>
        <VirtualTryOn /> {/* Nhúng tính năng thử đồ trực tuyến */}
      </div>
    </div>
  );
};

export default Home;
