// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/home';
import Login from './components/login';
import Register from './components/register';
import Navbar from './components/user/navbar';
import ProductList from './components/user/productList';
import ProductDetail from './components/user/productDetail';
import Cart from './components/user/cart';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';

const App = () => {
  const [isCartVisible, setIsCartVisible] = useState(false);
  const [cartItems, setCartItems] = useState([
    {
      id: 1,
      name: 'Sản phẩm 1',
      image: 'https://via.placeholder.com/50',
      price: 29.99,
      quantity: 2,
    },
    {
      id: 2,
      name: 'Sản phẩm 2',
      image: 'https://via.placeholder.com/50',
      price: 15.99,
      quantity: 1,
    },
  ]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [products, setProducts] = useState([]); // Giả sử bạn có danh sách sản phẩm

  const toggleCartVisibility = () => {
    setIsCartVisible(!isCartVisible);
  };

  const handleRemoveItem = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
  };

  const viewProductDetail = (product) => {
    setSelectedProduct(product);
  };

  const addToCart = (product) => {
    // Logic thêm sản phẩm vào giỏ hàng
  };

  const backToList = () => {
    setSelectedProduct(null);
  };

  return (
    <Router>
      <div className="App">
        <header>
          <h1>Trang Chính</h1>
          <button onClick={toggleCartVisibility}>
            <FontAwesomeIcon icon={faShoppingCart} />
          </button>
        </header>
        <Navbar cartCount={cartItems.length} />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/products"
            element={
              selectedProduct ? (
                <ProductDetail product={selectedProduct} addToCart={addToCart} onBack={backToList} />
              ) : (
                <ProductList products={products} onViewDetail={viewProductDetail} />
              )
            }
          />
        </Routes>
        <Cart
          isVisible={isCartVisible}
          onClose={() => setIsCartVisible(false)}
          cartItems={cartItems}
          onRemove={handleRemoveItem}
        />
      </div>
    </Router>
  );
};

export default App;
