// src/components/Cart.js
import React from 'react';
import '../../css/cart.css';

const Cart = ({ isVisible, onClose, cartItems, onRemove }) => {
  if (!isVisible) return null;

  const totalAmount = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div className="cart-overlay">
      <div className="cart-content">
        <button className="cart-close" onClick={onClose}>X</button>
        <h2>Giỏ Hàng</h2>
        {cartItems.length === 0 ? (
          <p>Giỏ hàng của bạn đang trống.</p>
        ) : (
          <>
            <div className="cart-items">
              {cartItems.map((item) => (
                <div key={item.id} className="cart-item">
                  <img src={item.image} alt={item.name} className="cart-item-image" />
                  <div className="cart-item-details">
                    <p className="cart-item-name">{item.name}</p>
                    <p className="cart-item-price">{item.quantity} x ${item.price.toFixed(2)}</p>
                  </div>
                  <button className="cart-item-remove" onClick={() => onRemove(item.id)}>Xóa</button>
                </div>
              ))}
            </div>
            <div className="cart-total">
              <p>Tổng cộng: ${totalAmount.toFixed(2)}</p>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Cart;
