import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './components/home';
import Login from './components/login';
import Register from './components/register';
import Navbar from './components/user/navbar';
import ProductDetail from './components/user/productDetail';
import Cart from './components/user/cart';
import VideoPlayer from './components/video';
import ProductCarousel from './components/user/productCarousel';

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  const products = [
    { id: 1, name: 'Áo thun', price: 200000, image: 'https://ithk-pro-itmall-item.oss-cn-hongkong.aliyuncs.com/2/product/0ZXTEM110323NWHX/0ZXTEM110323NWHX-pdp-1.jpg' },
    { id: 2, name: 'Quần jeans', price: 500000, image: 'https://ithk-pro-itmall-item.oss-cn-hongkong.aliyuncs.com/2/product/0ZXSWM115304NGRX/0ZXSWM115304NGRX-pdp-1.jpg' },
    { id: 3, name: 'Áo khoác', price: 700000, image: 'https://ithk-pro-itmall-item.oss-cn-hongkong.aliyuncs.com/2/product/0ZXLTM111015NGYX/0ZXLTM111015NGYX-pdp-1.jpg' },
    { id: 4, name: 'Áo thun', price: 200000, image: 'https://ithk-pro-itmall-item.oss-cn-hongkong.aliyuncs.com/2/product/0ZXTEM110323NWHX/0ZXTEM110323NWHX-pdp-1.jpg' },
    { id: 5, name: 'Quần jeans', price: 500000, image: 'https://ithk-pro-itmall-item.oss-cn-hongkong.aliyuncs.com/2/product/0ZXSWM115304NGRX/0ZXSWM115304NGRX-pdp-1.jpg' },
    { id: 6, name: 'Áo khoác', price: 700000, image: 'https://ithk-pro-itmall-item.oss-cn-hongkong.aliyuncs.com/2/product/0ZXLTM111015NGYX/0ZXLTM111015NGYX-pdp-1.jpg' },
  ];

  const addToCart = (product) => {
    setCartItems((prevCartItems) => {
      const existingProductIndex = prevCartItems.findIndex(item => item.id === product.id);
      if (existingProductIndex !== -1) {
        const updatedCartItems = [...prevCartItems];
        updatedCartItems[existingProductIndex].quantity += 1;
        return updatedCartItems;
      } else {
        return [...prevCartItems, { ...product, quantity: 1 }];
      }
    });
  };

  return (
    <div>
      <Navbar cartCount={cartItems.length} />
      <Routes>
        <Route path="/" element={<ProductCarousel products={products} />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/products/:id" element={<ProductDetail products={products} addToCart={addToCart} />} />
        <Route path="/cart" element={<Cart cartItems={cartItems} onRemove={(id) => setCartItems(cartItems.filter(item => item.id !== id))} />} />
        <Route path="/video" element={<VideoPlayer />} />
      </Routes>
    </div>
  );
};

const AppWrapper = () => (
  <Router>
    <App />
  </Router>
);

export default AppWrapper;
