import React, { useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import '../../css/productList.css';

const ProductList = ({ products }) => {
  const listRef = useRef(null);
  const [isDragging, setIsDragging] = useState(false);
  const [startX, setStartX] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.pageX - listRef.current.offsetLeft);
    setScrollLeft(listRef.current.scrollLeft);
  };

  const handleMouseLeave = () => {
    setIsDragging(false);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;
    e.preventDefault();
    const x = e.pageX - listRef.current.offsetLeft;
    const walk = (x - startX) * 3; // Tăng tốc độ cuộn
    listRef.current.scrollLeft = scrollLeft - walk;
  };

  const handleNextClick = () => {
    listRef.current.scrollBy({ left: 200, behavior: 'smooth' });
  };

  const handlePrevClick = () => {
    listRef.current.scrollBy({ left: -200, behavior: 'smooth' });
  };

  return (
    <div className="products-section">
      <button className="arrow prev" onClick={handlePrevClick}>&#10094;</button>
      <div
        className="product-list"
        ref={listRef}
        onMouseDown={handleMouseDown}
        onMouseLeave={handleMouseLeave}
        onMouseUp={handleMouseUp}
        onMouseMove={handleMouseMove}
      >
        {products.map((product) => (
          <div key={product.id} className="product-item">
            <Link to={`/products/${product.id}`} className="product-link">
              <img src={product.image} alt={product.name} className="product-image" />
            </Link>
            <div className="product-info">
              <Link to={`/products/${product.id}`} className="product-link">
                <h3 className="product-name">{product.name}</h3>
              </Link>
              <p className="product-price">{product.price.toLocaleString()} VND</p>
            </div>
          </div>
        ))}
      </div>
      <button className="arrow next" onClick={handleNextClick}>&#10095;</button>
    </div>
  );
};

export default ProductList;
