import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';
import '../../css/navbar.css';

const Navbar = ({ cartCount }) => {
  const navigate = useNavigate();

  const handleCartClick = () => {
    navigate('/cart'); // Điều hướng đến trang giỏ hàng
  };

  return (
    <nav>
      <div className="logo">
        {/* Sử dụng thẻ img để hiển thị logo */}
        <img 
          src="/img/heat_factory_logo_941df211-8a02-4325-ba30-62c2e83a4da1_210x-removebg-preview.png" 
          alt="Logo"
          className="logo-img"
        />
      </div>
      <ul>
        <li><a href="/">Trang Chủ</a></li>
        <li><a href="/login">Đăng Nhập</a></li>
        <li><a href="/register">Đăng Ký</a></li>
        <li><a href="/products">Sản Phẩm</a></li>
      </ul>
      <div className="cart" onClick={handleCartClick}>
        <FontAwesomeIcon icon={faShoppingCart} size="lg" />
        {cartCount > 0 && (
          <div className="cart-count">{cartCount}</div>
        )}
      </div>
    </nav>
  );
let lastScrollTop = 0; // Vị trí cuộn trước đó
const navbar = document.querySelector("nav"); // Lấy phần tử navbar

window.addEventListener("scroll", function () {
  let scrollTop = window.pageYOffset || document.documentElement.scrollTop;

  if (scrollTop > lastScrollTop) {
    // Nếu người dùng cuộn xuống
    navbar.style.top = "-80px"; // Ẩn navbar (điều chỉnh theo chiều cao của navbar)
  } else {
    // Nếu người dùng cuộn lên
    navbar.style.top = "0"; // Hiện lại navbar
  }

  lastScrollTop = scrollTop; // Cập nhật lại vị trí cuộn cuối cùng
});
};

export default Navbar;
